import { Component, ViewChild } from '@angular/core';
import { NavController, NavParams, ViewController, ActionSheetController } from 'ionic-angular';
import { FormBuilder, FormGroup, FormArray } from '@angular/forms';

import { Project } from '../../models/project'

import { Camera } from 'ionic-native';


@Component({
  selector: 'page-project-detail',
  templateUrl: 'project-detail.html'
})
export class ProjectDetailPage {
  @ViewChild('fileInput') fileInput;

  selectedProject: Project;

  projectForm: FormGroup;

  isReadyToSave: boolean;



  constructor(
    public navCtrl: NavController, 
    public navParams: NavParams, 
    public actionSheetCtrl: ActionSheetController, 
    public viewCtrl: ViewController, 
    private fb: FormBuilder) {

    this.selectedProject = navParams.get('project');
    this.createForm();

  }

    createForm() {
    this.projectForm = this.fb.group({
      name: [this.selectedProject.name ],
      bullet1: [this.selectedProject.bullet1],
      bullet2: [this.selectedProject.bullet2],
      bullet3: [this.selectedProject.bullet3],
      photos: this.fb.array([])
    });

    this.setPhotos(this.selectedProject.photos);

    // Watch the form for changes, and
    this.projectForm.valueChanges.subscribe((v) => {
      this.isReadyToSave = this.projectForm.valid;
    });
  }

  initPhoto() {
    // initialize our photo
    return this.fb.group({
        photo: ['']
    });
  }


  get photos(): FormArray {
    return this.projectForm.get('photos') as FormArray;
  };

  setPhotos(photos: any[]) {
    if(photos.length < 1){
      photos.push({ photo: ''});
    }
    console.log(photos);
    const photoFGs = photos.map(photo => this.fb.group(photo));
    const projectFormArray = this.fb.array(photoFGs);
    this.projectForm.setControl('photos', projectFormArray);
  }


  addPhoto() {
    // add photo to the list
    const control = <FormArray>this.projectForm.controls['photos'];
    control.push(this.initPhoto());
    console.log("Photos", this.projectForm.get('photos').value)
  }

  removePhoto(i: number) {
      // remove photo from the list
      const control = <FormArray>this.projectForm.controls['photos'];
      control.removeAt(i);
  }

  //PICTURE SELECTION
  presentActionSheet(photoNum) {
    let actionSheet = this.actionSheetCtrl.create({
      title: 'Select Image Source',
      buttons: [
        {
          text: 'Load from Library',
          handler: () => {
            this.takePicture(Camera.PictureSourceType.PHOTOLIBRARY, false, photoNum);
          }
        },
        {
          text: 'Use Camera',
          handler: () => {
            this.takePicture(Camera.PictureSourceType.CAMERA, true, photoNum);
          }
        },
        {
          text: 'Cancel',
          role: 'cancel'
        }
      ]
    });
    actionSheet.present();
  }

  takePicture(sourceType, saveToAlbum, photoNum) {
    let monsterImage = 'data:image/png;base64,';
    let cameraOptions = {
      sourceType: sourceType,
      destinationType: Camera.DestinationType.DATA_URL,
      saveToPhotoAlbum: saveToAlbum,     
      targetWidth: 300,
      targetHeight: 300,
      correctOrientation: true,
    }
    if (Camera['installed']()) {
      Camera.getPicture(cameraOptions)
      .then((data) => {
        this.projectForm.patchValue({ 'photo': 'data:image/jpg;base64,' +  data });
      }, (err) => {
        alert('Unable to load photo');
      })
    } else {
        // alert('Camera not available');
        this.projectForm.value.photos[photoNum].photo = monsterImage; //patchValue({ 'photo': monsterImage });
    }
  }



  
  
  //MODAL ACTIONS
  /**
   * The user cancelled, so we dismiss without sending data back.
   */
  cancel() {
    this.viewCtrl.dismiss();
  }

  /**
   * The user is done and wants to create the district, so return it
   * back to the presenter.
   */
  save() {
    if(!this.projectForm.valid) { return; }
    this.viewCtrl.dismiss(this.projectForm.value);
    console.log(this.projectForm.value);
  }

  }


